import 'package:flutter/material.dart';import 'package:flutter_hive_database/ui/home_page.dart';import '../../ui/auth_login.dart';class RouteGenerator{  static final  RouteGenerator  _generate=RouteGenerator.init();  static  RouteGenerator get router=> _generate;  RouteGenerator.init();  Route? onGenerator(RouteSettings  settings){        switch(settings.name){          case  '/':            return _navigate(HomePage());          case 'login':            return _navigate(AuthLoginPage());          default:        }           return   null;     }     MaterialPageRoute  _navigate(Widget  widget){       return  MaterialPageRoute(builder: (context)=> widget);     }     }