import 'package:hive_flutter/hive_flutter.dart';class  AuthDBService{  Future<void>  openBox() async{    var  box=await   Hive.openBox("todo");  }  Future<void>  writeToDB(String userData) async{    await  Hive.box("todo").add(userData);    if(!await find(userData)){      await  Hive.box("todo").add(userData);    }else{      print("Bu  ma'lumot  allaqachon  mavjud");    }  }  Future<dynamic>  getTodos() async{    try{      await  openBox();      var  box=Hive.box("todo");      return box.values.toList();    }catch(e){      return  e.toString();    }  }  Future<void>  deleteItem(int index) async{    await openBox();    await  Hive.box("todo").deleteAt(index);    return;  }  Future<void>  updateItem( int index,String newValue) async{    await openBox();    await  Hive.box("todo").putAt( index,newValue );    return;  }  Future<bool>   find(String value) async{    await  openBox();    bool  isFound=false;    Hive.box("todo").values.where( (element){      if(element == value){        isFound=true;        return  isFound;      }else{        return isFound;      }    });    return isFound;  }}