import 'package:hive_flutter/hive_flutter.dart';class  DBService{  Box?  db_mobileBox;   Future<void>  openBox() async{        db_mobileBox=  await  Hive.openBox("db_mobile");   }  Future<void> writeToDB(String  userData) async{        await  db_mobileBox!.add(userData);  }  Future<dynamic>  getDataMobile()async{        try{             await  openBox();             return db_mobileBox!.values.toList();        }catch(e){             return  e.toString();        }  }   Future<void>  deleteItem(int  index) async {        await  openBox();        await  db_mobileBox!.deleteAt(index);        return;   }    Future<void> updateItem(int index,String value) async{         await  openBox();         await db_mobileBox!.putAt(index, value);         return;    }}