import 'package:flutter/material.dart';class FormPage extends StatefulWidget {   FormPage({Key? key}) : super(key: key);  @override  State<FormPage> createState() => _FormPageState();}class _FormPageState extends State<FormPage> {  GlobalKey<FormState>  formKey=GlobalKey<FormState>();  @override  Widget build(BuildContext context) {    return  Scaffold(        appBar:   AppBar(title:  const  Text("Problem  Solution"),),        body:  Form(            key: formKey ,            child:  Column(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: [                 TextFormField(                   decoration:   InputDecoration(hintText: "Ismingiz: "),                   validator: (v){                      if(v!.isEmpty){                         return  " Bosh   qoldirmang";                      }else{                        return null;                      }                   },                 ),                 TextFormField(                   decoration:   InputDecoration(hintText: "Familyangiz: "),                   validator: (v){                      if(v!.isEmpty){                         return  "Bosh qoldirmang";                      }else{                        return null;                      }                   },                 ),                 TextFormField(                   decoration:   InputDecoration(hintText: "Sharifingiz: "),                   validator: (v){                      if(v!.isEmpty){                         return  "Bosh qoldirmang";                      }else{                        return null;                      }                   },                 ),                 TextFormField(                   decoration:   InputDecoration(hintText: "Manzilingiz: "),                   validator: (v){                      if(v!.isEmpty){                         return  "Bosh qoldirmang";                      }else{                        return null;                      }                   },                 )            ],          ),        ),      floatingActionButton:  FloatingActionButton(onPressed: (){            if(formKey.currentState!.validate()){                print("OK ");            }      },      child:  Text("Confirm"),),    );  }}